import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

export default class Header extends Component {
  render() {
    return (
      <div className="specials">
        <h1>Tu Restaurante</h1>
        <h3>Plaza Mayor, Mondejar 19110 (Guadalajara)</h3>
        <h3> tel. xxx xxx xxx email: xxx@mail.com</h3>
        <br>
        </br>
        <div>
            <button onClick={() => this.props.onIncrement(this.props.item)} className="btn btn-lg btn-outline-secondary"> 
            Ensaladas
            </button>
        </div>
      </div>
    );
  }
}
